import React, { Component } from 'react';
import { connect } from 'react-redux';
import { requestPosts } from '../actions/index';
import { Route, Switch } from 'react-router-dom';
import { push } from 'react-router-redux';


import './App.css';
import VisiblePostList from './VisiblePostList';


const mapStateToProps = (state) => {
  return {
    products: state.products,
    hairstyles: state.hairstyles,
    isPending: state.isPending
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onRequestPosts: () => dispatch(requestPosts())
  }
}


class App extends Component {

  componentDidMount(){
    this.props.onRequestPosts();
  }

  render() {

    return (
      <div>
        <Switch>
          <Route path="/" component={VisiblePostList} />
        </Switch>
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App)
