import React from 'react';
import { Link } from 'react-router-dom';
import { connect } from 'react-redux';



class Login extends React.Component {
	constructor(props) {
    	super(props);

    	this.state = {
    		user: {
    			first_name: '',
                last_name: '',
                username: '',
                password: ''
    		},
      		username: '',
      		password: '',
      		submitted: false
    	};

  		this.handleChange = this.handleChange.bind(this);
  		this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleLoginChange(e) {
		const { name, value } = e.target;
		this.setState({ [name]: value });
	}

	handleLoginSubmit(e) {
		e.preventDefault();

		this.setState({ submitted: true });
        const { username, password } = this.state;
        // if (username && password) {
        //     this.props.login(username, password);
        // }
	}

	render() {
		// const { loggingIn } = this.props;
  		const { username, password, submitted } = this.state;

  		return (
  			<div className="login-form-container">
  				<form name="login-form" onSubmit={this.handleLoginSubmit}>
  					<div className={'form-group' + (submitted && !username ? ' has-error' : '')}>
                        <label htmlFor="username">Username</label>
                        <input type="text" className="form-control" name="username" value={username} onChange={this.handleLoginChange} />
                        {submitted && !username &&
                            <div className="help-block">Username is required</div>
                        }
                    </div>
                    <div className={'form-group' + (submitted && !password ? ' has-error' : '')}>
                        <label htmlFor="password">Password</label>
                        <input type="password" className="form-control" name="password" value={password} onChange={this.handleLoginChange} />
                        {submitted && !password &&
                            <div className="help-block">Password is required</div>
                        }
                    </div>
                    <div className="form-group">
                        <button className="login-btn">Login</button>          
                    </div>
  				</form>
  			</div>
  		);
	}
}

export default Login
