import {
  REQUEST_POSTS_PENDING,
  REQUEST_POSTS_SUCCESS,
  REQUEST_POSTS_FAILED
} from '../constants'

export const requestPosts = () => (dispatch) => {
  dispatch({ type: REQUEST_POSTS_PENDING })
  fetch('http://localhost:3000/products/')
    .then(res => res.json())
    .then(data => {
      let products = [];
      let hairstyles = data.filter(post => {
        if (post.hairtype == null)
          products.push(post)
        else
          return post;
      });
      dispatch({
        type: REQUEST_POSTS_SUCCESS,
        payload: {hairstlyes: hairstyles, products: products}
      });
    })
    .catch(error => dispatch({ type: REQUEST_POSTS_FAILED, payload: error }))
}
