import React, { Component } from 'react';
import { connect } from 'react-redux';
import { requestPosts } from '../actions/index';
import { Route, Switch } from 'react-router-dom';
import { push } from 'react-router-redux';


import './App.css';
import VisiblePostList from './VisiblePostList';


const mapStateToProps = (state) => {
  return {
    products: state.requestPosts.products,
    hairstyles: state.requestPosts.hairstyles,
    isPending: state.requestPosts.isPending
  }
}

const mapDispatchToProps = (dispatch) => {
  return {
    onRequestPosts: () => dispatch(requestPosts())
  }
}


class App extends Component {

  componentDidMount(){
    this.props.onRequestPosts();
  }

  render() {

    return (
      <div>
        <Switch>
          <Route 
            path="/products" 
            render={props => <VisiblePostList {...props} products={this.props.products} />}
          />
        </Switch>
      </div>
    );
  }
}

export default connect(mapStateToProps, mapDispatchToProps)(App)
